PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
DROP TABLE IF EXISTS collection;
DROP TABLE IF EXISTS fijalist_collection;
DROP TABLE IF EXISTS fijalist_tag;
DROP TABLE IF EXISTS fijalist;
DROP TABLE IF EXISTS tag;
DROP TABLE IF EXISTS user;
CREATE TABLE fijalist (
	id INTEGER NOT NULL, 
	title VARCHAR(255) NOT NULL, 
	description VARCHAR(500), 
	cover_image VARCHAR(255), 
	content JSON NOT NULL, 
	created_at DATETIME, 
	updated_at DATETIME, 
	PRIMARY KEY (id)
);
CREATE TABLE tag (
	id INTEGER NOT NULL, 
	name VARCHAR(50) NOT NULL, 
	PRIMARY KEY (id), 
	UNIQUE (name)
);
CREATE TABLE user (
	id INTEGER NOT NULL, 
	username VARCHAR(80) NOT NULL, 
	email VARCHAR(200) NOT NULL, 
	password_hash VARCHAR(200) NOT NULL, 
	profile_picture VARCHAR(200), 
	created_at DATETIME, 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	UNIQUE (username), 
	UNIQUE (email)
);
CREATE TABLE collection (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	name VARCHAR(255) NOT NULL, 
	description VARCHAR(500), 
	is_private BOOLEAN, 
	created_at DATETIME, 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES user (id)
);
CREATE TABLE fijalist_tag (
	id INTEGER NOT NULL, 
	fijalist_id INTEGER NOT NULL, 
	tag_id INTEGER NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(fijalist_id) REFERENCES fijalist (id), 
	FOREIGN KEY(tag_id) REFERENCES tag (id)
);
CREATE TABLE fijalist_collection (
	id INTEGER NOT NULL, 
	fijalist_id INTEGER NOT NULL, 
	collection_id INTEGER NOT NULL, 
	added_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(fijalist_id) REFERENCES fijalist (id), 
	FOREIGN KEY(collection_id) REFERENCES collection (id)
);
COMMIT;
